/* Thiết lập Font */
@font-face
	{font-family:"Cambria Math";}
@font-face
	{font-family:DengXian;}
@font-face
	{font-family:Calibri;}
@font-face
	{font-family:"Calibri Light";}
@font-face
	{font-family:"Noto Sans Symbols";}
@font-face
	{font-family:"DengXian Light";}
@font-face
	{font-family:Georgia;}
@font-face
	{font-family:"Segoe UI";}
@font-face
	{font-family:"UTM Avenida";}
@font-face
	{font-family:"\@DengXian";}
@font-face
	{font-family:"\@DengXian Light";}

/* Global link styles */
a:link {color:blue; text-decoration:underline;}
a:visited {color:purple; text-decoration:underline;}
a:hover {color:red;}

/* Định dạng cơ bản cho đoạn văn bản (A4, 14pt, 1.5 line, căn đều) */
p.MsoNormal, li.MsoNormal, div.MsoNormal {
    margin-top: 0in;
    margin-right: 0in;
    margin-bottom: 12pt; /* Khoảng cách giữa các đoạn */
    margin-left: 0in;
    line-height: 150%; /* Giãn dòng 1.5 */
    font-size: 14.0pt; /* Cỡ chữ 14 */
    font-family: "Times New Roman", serif;
    text-align: justify; /* Căn đều hai bên */
    text-indent: 0in; /* Mặc định không thụt lề, sẽ dùng inline style cho đoạn có thụt lề */
    color: black;
}

/* Định dạng Tiêu đề cấp 1 (CHƯƠNG) */
h1 {
    margin-top: 24pt;
    margin-bottom: 12pt;
    line-height: 107%;
    page-break-after: avoid;
    font-size: 20.0pt;
    font-family: "Times New Roman", serif;
    color: black;
    text-align: center;
    font-weight: bold;
}

/* Định dạng Tiêu đề cấp 2 (Phần) */
h2 {
    margin-top: 18pt;
    margin-bottom: 8pt;
    line-height: 107%;
    page-break-after: avoid;
    font-size: 16.0pt;
    font-family: "Times New Roman", serif;
    color: black;
    text-align: center;
    font-weight: bold;
}

/* Định dạng Chú thích hình ảnh */
.image-caption {
    margin-top: 4pt;
    margin-bottom: 12pt;
    text-align: center;
    font-size: 12.0pt;
    font-family: "Times New Roman", serif;
    font-style: italic;
    line-height: 150%;
}

/* Định dạng cho bảng chứa hình ảnh và chú thích bên cạnh (Sơ đồ Eratosthenes) */
table.image-layout-table {
    border-collapse: collapse;
    width: 100%;
    margin: 12pt auto; /* Căn giữa */
}

table.image-layout-table td {
    border: none;
    padding: 0;
}

table.image-layout-table .image-cell {
    text-align: center;
    padding: 5.4pt;
}

table.image-layout-table .caption-cell {
    border: solid black 1.0pt; /* Giữ khung viền cho chú thích */
    padding: 10pt;
    vertical-align: top;
}

/* Định nghĩa trang A4 (595.3pt x 841.9pt) và lề */
@page WordSection4
	{size:595.3pt 841.9pt; /* Kích thước A4 */
	margin:56.7pt 56.7pt 56.7pt 85.05pt;} /* Lề: Top 2cm, Right 2cm, Bottom 2cm, Left 3cm */
div.WordSection4
	{page:WordSection4;}
